import { useState } from 'react';

function Navbar() {
  const [open, setOpen] = useState(false);
  const navLinks = ['Gallery', 'Services', 'Materials', 'About', 'Contact'];
  return (
    <header className="bg-white shadow-sm sticky top-0 z-50">
      <nav className="flex justify-between items-center max-w-7xl mx-auto py-4 px-4 sm:px-6 lg:px-8">
        <div className="font-serif font-bold text-xl">Marble Gallery</div>
        {/* Desktop nav */}
        <div className="hidden md:flex items-center gap-6">
          <ul className="flex gap-3">
            {navLinks.map((item) => (
              <li key={item}>
                <a href={`#${item.toLowerCase()}`} className="text-sm font-medium text-gray-600 hover:text-black transition">
                  {item}
                </a>
              </li>
            ))}
          </ul>
          <button className="px-4 py-1 bg-white border rounded-lg shadow-sm hover:shadow-md text-sm font-medium ml-4 transition">Search</button>
        </div>
        {/* Mobile hamburger */}
        <div className="md:hidden flex items-center">
          <button
            className="p-2 rounded focus:outline-none focus:ring-2 focus:ring-gray-300"
            onClick={() => setOpen((v) => !v)}
            aria-label="Open navigation menu"
          >
            <svg className="w-6 h-6" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" d={open ? 'M6 18L18 6M6 6l12 12' : 'M4 6h16M4 12h16M4 18h16'} />
            </svg>
          </button>
        </div>
      </nav>
      {/* Mobile menu */}
      {open && (
        <div className="md:hidden bg-white shadow-sm border-t border-gray-100">
          <ul className="flex flex-col gap-2 px-6 py-4">
            {navLinks.map((item) => (
              <li key={item}>
                <a
                  href={`#${item.toLowerCase()}`}
                  className="block py-2 text-sm font-medium text-gray-600 hover:text-black transition"
                  onClick={() => setOpen(false)}
                >
                  {item}
                </a>
              </li>
            ))}
            <li>
              <button className="w-full px-4 py-2 bg-white border rounded-lg shadow-sm hover:shadow-md text-sm font-medium mt-2 transition">Search</button>
            </li>
          </ul>
        </div>
      )}
    </header>
  );
}
export default Navbar;
