import { FaInstagram, FaFacebookF, FaTwitter } from 'react-icons/fa';

function Footer({ lang }) {
  return (
    <footer className="bg-white border-t border-gray-200 mt-24 py-8 sm:py-12" style={{ fontFamily: lang === 'ar' ? 'Cairo, sans-serif' : undefined }}>
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center text-gray-500">
        <div className="flex flex-wrap justify-center gap-4 sm:gap-6 mb-6">
          <a href="#" className="text-2xl hover:text-brand-dark transition"><FaInstagram /></a>
          <a href="#" className="text-2xl hover:text-brand-dark transition"><FaFacebookF /></a>
          <a href="#" className="text-2xl hover:text-brand-dark transition"><FaTwitter /></a>
        </div>
        <div className="flex flex-wrap justify-center gap-4 sm:gap-6 mb-6 text-sm">
          <a href="#" className="hover:text-black transition">{lang === 'ar' ? 'سياسة الخصوصية' : 'Privacy Policy'}</a>
          <a href="#" className="hover:text-black transition">{lang === 'ar' ? 'شروط الخدمة' : 'Terms of Service'}</a>
        </div>
        <div className="text-sm break-words px-2">{lang === 'ar' ? '© 2024 معرض الرخام. جميع الحقوق محفوظة.' : '© 2024 Marble Gallery. All rights reserved.'}</div>
      </div>
    </footer>
  );
}
export default Footer;
