const projects = [
  {
    title: 'Luxury Kitchen Renovation',
    img: 'https://images.unsplash.com/photo-1506744038136-46273834b3fb?auto=format&fit=crop&w=600&q=80',
  },
  {
    title: 'Modern Bathroom Design',
    img: 'https://images.unsplash.com/photo-1512918728675-ed5a9ecdebfd?auto=format&fit=crop&w=600&q=80',
  },
  {
    title: 'Elegant Living Room Transformation',
    img: 'https://images.unsplash.com/photo-1460518451285-97b6aa326961?auto=format&fit=crop&w=600&q=80',
  },
];

function FeaturedProjects() {
  return (
    <section className="my-16">
      <h2 className="text-2xl font-bold font-serif text-center mb-4">Featured Projects</h2>
      <div className="flex flex-col md:flex-row gap-6 justify-center">
        {projects.map((p, i) => (
          <div key={i} className="bg-white rounded-2xl shadow-md p-4 flex-1 flex flex-col items-center max-w-xs mx-auto">
            <img src={p.img} alt={p.title} className="rounded-lg w-full h-48 object-cover" />
            <div className="text-sm font-semibold text-gray-800 mt-2 text-center">{p.title}</div>
          </div>
        ))}
      </div>
    </section>
  );
}
export default FeaturedProjects;
