function Navbar() {
  return (
    <header className="bg-white shadow-sm sticky top-0 z-50">
      <nav className="flex justify-between items-center max-w-7xl mx-auto py-4 px-4 sm:px-6 lg:px-8">
        <div className="font-serif font-bold text-xl">Marble Gallery</div>
        <div className="flex items-center gap-6">
          <ul className="flex gap-3">
            {['Gallery', 'Services', 'Materials', 'About', 'Contact'].map((item) => (
              <li key={item}>
                <a href={`#${item.toLowerCase()}`} className="text-sm font-medium text-gray-600 hover:text-black transition">
                  {item}
                </a>
              </li>
            ))}
          </ul>
          <button className="px-4 py-1 bg-white border rounded-lg shadow-sm hover:shadow-md text-sm font-medium ml-4 transition">Search</button>
        </div>
      </nav>
    </header>
  );
}
export default Navbar;
