import { useState, useRef } from 'react';

const ALLOWED_TYPES = ['image/jpeg', 'image/png', 'image/webp'];
const MAX_SIZE = 5 * 1024 * 1024; // 5MB

function AdminDashboard({ onAddImage }) {
  const [tag, setTag] = useState('');
  const [lang, setLang] = useState('en');
  const [message, setMessage] = useState('');
  const [fileName, setFileName] = useState('');
  const [preview, setPreview] = useState('');
  const fileInputRef = useRef(null);

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    if (!ALLOWED_TYPES.includes(file.type)) {
      setMessage('Error: Only JPG, PNG, and WebP images are allowed');
      return;
    }

    if (file.size > MAX_SIZE) {
      setMessage('Error: File size must be less than 5MB');
      return;
    }

    setFileName(file.name);
    // Create preview URL
    const previewUrl = URL.createObjectURL(file);
    setPreview(previewUrl);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const file = fileInputRef.current?.files[0];
    if (!file || !tag) {
      setMessage('Please select an image and add a tag');
      return;
    }
    
    // Create unique filename
    const timestamp = Date.now();
    const ext = file.name.split('.').pop();
    const newFileName = `${timestamp}.${ext}`;
    const imagePath = `/images/${newFileName}`;

    // Here we would typically upload the file to a server
    // For now, we'll just add it to the state
    onAddImage({ 
      src: imagePath,
      tag, 
      lang,
      originalName: file.name 
    });

    setMessage('Image added!');
    setTag('');
    setFileName('');
    setPreview('');
    if (fileInputRef.current) fileInputRef.current.value = '';
  };

  return (
    <div className="max-w-2xl mx-auto my-16 p-8 bg-white rounded shadow">
      <h2 className="text-2xl font-bold mb-6">Admin Dashboard</h2>
      <form onSubmit={handleSubmit} className="space-y-4 mb-8">
        <div>
          <label className="block mb-1 font-medium">Upload Image</label>
          <input
            type="file"
            ref={fileInputRef}
            onChange={handleFileChange}
            accept="image/*"
            className="w-full px-3 py-2 border rounded"
          />
          {preview && (
            <div className="mt-2">
              <img src={preview} alt="Preview" className="w-40 h-40 object-cover rounded" />
            </div>
          )}
          {fileName && <p className="mt-2 text-sm text-gray-600">Selected: {fileName}</p>}
        </div>
        <div>
          <label className="block mb-1 font-medium">Tag/Details</label>
          <input type="text" value={tag} onChange={e => setTag(e.target.value)} className="w-full px-3 py-2 border rounded" placeholder="e.g. Kitchen" />
        </div>
        <div>
          <label className="block mb-1 font-medium">Language</label>
          <select value={lang} onChange={e => setLang(e.target.value)} className="w-full px-3 py-2 border rounded">
            <option value="en">English</option>
            <option value="ar">Arabic</option>
          </select>
        </div>
        <button type="submit" className="bg-brand-dark text-white px-4 py-2 rounded">Add Image</button>
        {message && (
          <div className={`mt-2 ${message.startsWith('Error') ? 'text-red-600' : 'text-green-600'}`}>
            {message}
          </div>
        )}
      </form>
      <p className="mb-4">Add gallery photos with details for All Works page.</p>
    </div>
  );
}
export default AdminDashboard;
