function Hero() {
  return (
    <section id="services"
      className="w-full h-[500px] flex items-center justify-center rounded-2xl overflow-hidden bg-cover bg-center my-16"
      style={{
        backgroundImage:
          'url(https://images.unsplash.com/photo-1618762638822-20586945a8a7?auto=format&fit=crop&w=1200&q=80)',
      }}
    >
      <div className="text-center bg-black bg-opacity-30 p-10 rounded-xl flex flex-col items-center">
        <h1 className="font-serif text-white text-4xl md:text-5xl font-extrabold drop-shadow mb-4">
          Timeless Creations in Marble
        </h1>
        <p className="font-sans text-white text-base md:text-lg mt-2 opacity-90 max-w-xl mx-auto">
          Discover the artistry and elegance of our marble creations, crafted to perfection for your home.
        </p>
        <button className="mt-6 px-6 py-2 bg-white text-black rounded-lg shadow hover:bg-gray-100 font-medium transition">
          View Projects
        </button>
      </div>
    </section>
  );
}
export default Hero;
