import { useState } from 'react';

const images = [
  { src: 'https://picsum.photos/id/1018/400/300', tag: 'Kitchen' },
  { src: 'https://picsum.photos/id/1025/400/300', tag: 'Bathroom' },
  { src: 'https://picsum.photos/id/1035/400/300', tag: 'Living Room' },
  { src: 'https://picsum.photos/id/1041/400/300', tag: 'Kitchen' },
  { src: 'https://picsum.photos/id/1050/400/300', tag: 'Bathroom' },
  { src: 'https://picsum.photos/id/1062/400/300', tag: 'Living Room' },
];
const filters = ['All', 'Kitchen', 'Bathroom', 'Living Room'];

function Gallery() {
  const [active, setActive] = useState('All');
  const filtered = active === 'All' ? images : images.filter(img => img.tag === active);
  return (
    <section className="my-16">
      <h2 className="text-2xl font-bold font-serif text-center mb-6">Gallery</h2>
      <div className="flex gap-3 flex-wrap justify-center mb-8">
        {filters.map(f => (
          <button
            key={f}
            onClick={() => setActive(f)}
            className={`px-4 py-2 bg-white border rounded-full text-sm shadow transition hover:bg-gray-100 font-medium ${active === f ? 'border-brand-dark text-black' : 'text-gray-600'}`}
          >
            {f}
          </button>
        ))}
      </div>
      <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
        {filtered.map((img, i) => (
          <div
            key={i}
            className="relative group overflow-hidden rounded-lg shadow-sm hover:shadow-lg transition w-full h-48 cursor-pointer"
          >
            <img
              src={img.src}
              alt={img.tag}
              className="w-full h-full object-cover group-hover:scale-105 group-hover:brightness-75 transition-transform duration-300 ease-in-out"
            />
            <div className="absolute inset-0 flex items-center justify-center opacity-0 group-hover:opacity-100 transition-opacity duration-300">
              <span className="text-white text-lg font-semibold bg-black bg-opacity-40 px-4 py-2 rounded-xl shadow-lg border border-white">
                {img.tag}
              </span>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
}
export default Gallery;
